    private void btnEntryActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEntryActionPerformed
        // TODO add your handling code here:
        String usuario = jTextUser.getText();
        String pass = new String(jTextPass.getPassword());

        try {
            consultarUsuario(usuario, pass);
        } catch (ClassNotFoundException ex) {
            System.out.println("Error: " + ex.getMessage());
        }

    }//GEN-LAST:event_btnEntryActionPerformed

    public void consultarUsuario(String usuario, String pass) throws ClassNotFoundException {

        ConexionDB db = new ConexionDB();
        Connection conexion = db.conectar();

        String consulta = "SELECT * FROM estudiante WHERE id_estudiante = ? AND clave = ?";

        try {
            PreparedStatement preparedStatement = conexion.prepareStatement(consulta);
            preparedStatement.setString(1, usuario);
            preparedStatement.setString(2, pass);

            ResultSet resultSet = preparedStatement.executeQuery();

            if (resultSet.next()) {
                JOptionPane.showMessageDialog(null, "Inicio de sesi칩n exitoso");
            } else {
                JOptionPane.showMessageDialog(null, "Credenciales inv치lidas");
            }
        } catch (SQLException e) {
            System.out.println("Error al validar las credenciales: " + e.getMessage());
        } finally {
            try {
                conexion.close();
            } catch (SQLException e) {
                System.out.println("Error al cerrar la conexi칩n: " + e.getMessage());
            }
        }

    }
